import React, { useState } from 'react';

function SignInSignUpScreen() {
  const [signInData, setSignInData] = useState({ email: '', password: '' });
  const [signUpData, setSignUpData] = useState({ email: '', password: '' });
  const [formType, setFormType] = useState('sign-in');

  const handleSignInChange = (e) => {
    const { name, value } = e.target;
    setSignInData({ ...signInData, [name]: value });
  };

  const handleSignUpChange = (e) => {
    const { name, value } = e.target;
    setSignUpData({ ...signUpData, [name]: value });
  };

  const handleFormSwitch = (type) => {
    setFormType(type);
  };

  const handleSignInSubmit = (e) => {
    e.preventDefault();
    console.log('Sign In Data:', signInData);
    // You can add your sign in logic here
  };

  const handleSignUpSubmit = (e) => {
    e.preventDefault();
    console.log('Sign Up Data:', signUpData);
    // You can add your sign up logic here
  };

  return (
    <div>
      {formType === 'sign-in' ? (
        <div>
          <h2>Sign In</h2>
          <form onSubmit={handleSignInSubmit}>
            <input type="text" name="email" placeholder="Email" value={signInData.email} onChange={handleSignInChange} />
            <br />
            <input type="password" name="password" placeholder="Password" value={signInData.password} onChange={handleSignInChange} />
            <br />
            <button type="submit">Sign In</button>
          </form>
          <p>
            Don't have an account? <button onClick={() => handleFormSwitch('sign-up')}>Sign Up</button>
          </p>
        </div>
      ) : (
        <div>
          <h2>Sign Up</h2>
          <form onSubmit={handleSignUpSubmit}>
            <input type="text" name="email" placeholder="Email" value={signUpData.email} onChange={handleSignUpChange} />
            <br />
            <input type="password" name="password" placeholder="Password" value={signUpData.password} onChange={handleSignUpChange} />
            <br />
            <button type="submit">Sign Up</button>
          </form>
          <p>
            Already have an account? <button onClick={() => handleFormSwitch('sign-in')}>Sign In</button>
          </p>
        </div>
      )}
    </div>
  );
}

export default SignInSignUpScreen;
